define(['views/fieldinfo-view', 'services/fieldInfoService',], function(fieldInfoView, fieldInfoService){
    var externals = {};
    var internals = {};

    externals.start = function(){
        internals.bindEventHandlers();
        fieldInfoService.getFieldInfo(function(id){
            fieldInfoView.render(id);
        });
        
    };

    internals.bindEventHandlers = function(){
        //filmView.bind('campButtons', internals.buttonHandler);
    };

    internals.buttonHandler = function(event){
        console.log((event.currentTarget.id).split("camp-")[1])
        window.location.hash = "#fieldinfo"
    }
    
        


    return externals;
});