define(['views/field-view', 'services/fieldService','views/fieldinfo-view','views/login-view','services/userService'], function(fieldView, fieldService,fieldInfoView,loginView,userService){

    var internals = {
        handlers: {},
        elements: {}
    };
    var externals = {};

    externals.userId = "";

    internals.createLogin = () =>{

        return `<form >
        <label for="inputField">Enter Id:</label>
        <input type="number" id="inputField" name="inputField" required>
        <button type="button" onclick="getValue()">Login</button>
    </form>`

    }


    externals.renderForm = () =>{
        $(".containerer").empty();
        internals.elements.loginForm = $(internals.createLogin())
        $("#campos").append(internals.elements.loginForm)
    }

    getValue = () =>{
        externals.userId = document.getElementById("inputField").value;
        console.log(externals.userId);
        userService.AuthUser(externals.userId);
    }

    return externals;
})